create database minecraft;
use minecraft;

create table tb_world (
	id int primary key auto_increment,
    name varchar(255) unique not null
);

create table tb_player (
	id int primary key auto_increment,
    name varchar(255) unique not null
);

create table tb_shop_item (
	first_id int not null,
    second_id int not null,
    name varchar(255) not null,
    english_name varchar(255) not null,
    price int not null,
    group_amount int not null,
    primary key (first_id, second_id)
);

create table tb_shop_record (
	id int primary key auto_increment,
    player_id int not null,
    world_id int not null,
    item_first_id int not null,
    item_second_id int not null,
    amount int not null,
    type int not null,
    time timestamp not null,
    foreign key (player_id) references tb_player(id),
    foreign key (world_id) references tb_world(id),
    foreign key (item_first_id, item_second_id) references tb_shop_item(first_id, second_id)
);

create table tb_bank_account (
	id int primary key auto_increment,
    player_id int not null,
    world_id int not null,
    balance int not null,
    foreign key (player_id) references tb_player(id),
	foreign key (world_id) references tb_world(id)
);

create table tb_bank_account_record (
	id int primary key auto_increment,
    src int not null,
    dest int not null,
    amount double not null,
    time timestamp not null,
    note varchar(255),
    foreign key (src) references tb_bank_account(id),
    foreign key (dest) references tb_bank_account(id)
);


